//该文件用于创建Vuex中的store
//准备actions，用于响应组件中的动作
const actions={
    add:function(context,step){
        context.commit('ADD',step)
    },
    subtract:function(context,step){
        context.commit('SUBTRACT',step)
    },
    add_odd:function(context,step){
        context.commit('ADD_ODD',step)
    },
    wait_add:function(context,step,add_id){
        context.commit('WAIT_ADD',step)
    }
}
//准备mutations，用于操作数据(state)
const mutations={
    ADD:function(state,step){
        state.sum+=step
        
    },
    SUBTRACT:function(state,step){
        state.sum-=step
    },
    ADD_ODD:function(state,step){
        if(state.sum%2!==0){
            this.sum+=step
        }
    },
    WAIT_ADD:function(state,step){
        add_id=setInterval(() => {
                state.sum+=step
            }, 1000);
    
       
    }
}
//准备state ,用于存储数据
const state={
    sum:0,
}
//引入Vuex
import Vuex from 'vuex'
//引入Vue
import Vue from 'vue'
//应用Vuex插件
Vue.use(Vuex)
//创建store，并暴露store
export default new Vuex.Store(
    {
        actions:actions,
        mutations:mutations,
        state:state,

    }
)
