export default {
    install(Vue){
        //定义全局过滤器
        Vue.filter('timefmt',function(){
                return dayjs().format('YYYY-MM-DD HH:mm:ss')
            })
        //定义全局指令
        Vue.directives('fbind',{
            //指令和元素成功绑定时
                bind(e,b){
                    console.log(this)//此处的this是window
                    e.value=b.value
                    },
            //指令所在元素被插入页面时
                 inserted(e,b){
                     e.focus()
                    },
            //指令所在的模板被重新解析时
                update(e,b){
                       e.value=b.value
                   }
             })
        //定义混入时
        Vue.mixin()

    }
}
