//该文件用于创建Vuex中的store
//准备actions，用于响应组件中的动作
const actions={}
//准备mutations，用于操作数据(state)
const mutations={}
//准备state ,用于存储数据
const state={}
//引入Vue
import Vuex from 'vuex'
//创建store，并暴露store
export default new Vuex.Store(
    {
        actions:actions,
        mutations:mutations,
        state:state,

    }
)
