//该文件用于创建Vuex中的store
//准备actions，用于响应组件中的动作
const actions={
    add:function(context,step){
        context.commit('ADD',step)
    },
}
//准备mutations，用于操作数据(state)
const mutations={
    ADD:function(a,b){
        a.sum+=b
        console.log(a.sum)
    }
}
//准备state ,用于存储数据
const state={
    sum:0,
}
//引入Vuex
import Vuex from 'vuex'
//引入Vue
import Vue from 'vue'
//应用Vuex插件
Vue.use(Vuex)
//创建store，并暴露store
export default new Vuex.Store(
    {
        actions:actions,
        mutations:mutations,
        state:state,

    }
)
